import axios from '../../../axios'

// React Notification
import { NotificationManager } from 'react-notifications';

export const getArticleCategories = () => async dispatch =>{

    const res = await axios.get('category/article');
    dispatch ({ 
        type : 'GET_ARTICLE_CATEGORIES',
        payload : res.data
    }); 
}

